mapper.insert:
INSERT INTO code (
	codeGroup_seq
    ,codeAnother
    ,codeNameKor
    ,codeNameEng
    ,useNy
    ,turn
    ,content
    ,delNy
    ,varcharType1
    ,varcharType2
    ,varcharType3
    ,intType1
    ,intType2
    ,intType3
    ,registerDateTime
    ,modifyDateTime
)
values(
	#{codeGroup_seq}
	,#{codeAnother}
	,#{codeNameKor}
	,#{codeNameEng}
	,#{useNy}
	,#{turn}
	,#{content}
	,0
	,#{varcharType1}
	,#{varcharType2}
	,#{varcharType3}
	,#{intType1}
	,#{intType2}
	,#{intType3}
	,#{registerDateTime}
	,#{modifyDateTime}
)

	SELECT last_insert_id()

mapper.update:
UPDATE code
SET
    codeAnother = #{codeAnother}
    ,codeNameKor = #{codeNameKor}
    ,codeNameEng = #{codeNameEng}
    ,useNy = #{useNy}
    ,turn = #{turn}
    ,content = #{content}
    ,delNy = #{delNy}
    ,varcharType1 = #{varcharType1}
    ,varcharType2 = #{varcharType2}
    ,varcharType3 = #{varcharType3}
    ,intType1 = #{intType1}
    ,intType2 = #{intType2}
    ,intType3 = #{intType3}
    ,registerDateTime = #{registerDateTime}
    ,modifyDateTime = #{modifyDateTime}
WHERE 1=1
	AND seq= #{seq}
