mapper.sql:
FROM codeGroup a
WHERE 1=1
	AND delNy = #{shdelNy}

		AND codeGroupCode = #{shValue}
		AND codeGroupNameKor LIKE CONCAT('%',#{shValue},'%')
		AND codeGroupNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO codeGroup(
    codeGroupCode
    ,codeGroupCodeAnother
    ,codeGroupNameKor
    ,codeGroupNameEng
    ,useNy
    ,turn
    ,content
    ,delNy
    ,varcharType1
    ,varcharType2
    ,varcharType3
    ,intType1
    ,intType2
    ,intType3
    ,registerDateTime
    ,modifyDateTime
)
VALUES (
	#{codeGroupCode}
	,#{codeGroupCodeAnother}
	,#{codeGroupNameKor}
	,#{codeGroupNameEng}
	,#{useNy}
	,#{turn}
	,#{content}
	,#{delNy}
	,#{varcharType1}
	,#{varcharType2}
	,#{varcharType3}
	,#{intType1}
	,#{intType2}
	,#{intType3}
	,#{registerDateTime}
	,#{modifyDateTime}
)

	SELECT last_insert_id()

mapper.update:
UPDATE codeGroup
SET
    codeGroupCode = #{codeGroupCode}
    ,codeGroupCodeAnother = #{codeGroupCodeAnother}
    ,codeGroupNameKor = #{codeGroupNameKor}
    ,codeGroupNameEng = #{codeGroupNameEng}
    ,useNy = #{useNy}
    ,turn = #{turn}
    ,content = #{content}
    ,delNy = #{delNy} 
    ,varcharType1 = #{varcharType1}
    ,varcharType2 = #{varcharType2}
    ,varcharType3 = #{varcharType3}
    ,intType1 = #{intType1}
    ,intType2 = #{intType2}
    ,intType3 = #{intType3}
    ,modifyDateTime = #{modifyDateTime}
WHERE
	codeGroupCode = #{codeGroupCode}
