mapper.insert:
INSERT INTO jsMember(
	nickname
	,dob
	,gender
	,email
	,id
	,password
	,delNy
	,registerDateTime
)
VALUES(
	#{nickname}
	,#{dob}
	,#{gender}
	,#{email}
	,#{id}
	,#{password}
	,#{delNy}
	,#{registerDateTime}
)

	SELECT last_insert_id()

mapper.update:
UPDATE jsMember
SET
	nickname = #{nickname}
	,dob = #{dob}
	,gender = #{gender}
	,email = #{email}
	,id = #{id}
	,password = #{password}
	,delNy = #{delNy}
	,registerDateTime = #{registerDateTime}
WHERE
	seq=#{seq}
