mapper.select:
SELECT
	seq
FROM jsMember
WHERE 1=1
	AND id = #{id}
	AND dob = #{dob}
	AND email = #{email}
	AND delNy = 0

mapper.update:
UPDATE jsMember
SET
    password = #{password}
WHERE 1=1
	seq = #{seq}

mapper.insert:
INSERT INTO jsMember(
	nickname
	,dob
	,gender
	,email
	,id
	,password
	,delNy
)
VALUES(
	#{nickname}
	,#{dob}
	,#{gender}
	,#{email}
	,#{id}
	,#{password}
	,0
)

	SELECT last_insert_id()

INSERT INTO jsMember(
	nickname
	,gender
	,email
	,delNy
	,snsId
)
VALUES(
	#{nickname}
	,#{gender}
	,#{email}
	,0
	,#{snsId}
)

	SELECT last_insert_id()

mapper.update:
UPDATE jsMember
SET
    nickname = #{nickname}
    ,dob = #{dob}
    ,gender = #{gender}
    ,id = #{id}
    ,delNy = #{delNy} 
