	,gender
	,email
	,id
	,password
	,delNy
	,registerDateTime
)
VALUES(
	#{nickname}
	,#{dob}
	,#{gender}
	,#{email}
	,#{id}
	,#{password}
	,0
	,#{registerDateTime}
)

	SELECT last_insert_id()

mapper.update:
UPDATE jsMember
SET
	nickname = #{nickname}
	,dob = #{dob}
	,gender = #{gender}
	,email = #{email}
	,id = #{id}
	,delNy = #{delNy}
WHERE 
	seq = #{seq}
